CREATE PROCEDURE Proc_Employee_DeleteMultiple (IN id_list text)
SQL SECURITY INVOKER
COMMENT 'Thủ tục xóa nhiều bản ghi'
BEGIN
  DECLARE done int DEFAULT FALSE;
  DECLARE id char(36);
  DECLARE id_cursor CURSOR FOR
  SELECT
    CAST(token AS char(36))
  FROM (SELECT
      TRIM(SUBSTRING_INDEX (SUBSTRING_INDEX (id_list, ',', n.digit + 1), ',', -1)) AS token
    FROM (SELECT
        0 digit
      UNION ALL
      SELECT
        1
      UNION ALL
      SELECT
        2
      UNION ALL
      SELECT
        3
      UNION ALL
      SELECT
        4
      UNION ALL
      SELECT
        5
      UNION ALL
      SELECT
        6
      UNION ALL
      SELECT
        7
      UNION ALL
      SELECT
        8
      UNION ALL
      SELECT
        9) n
    WHERE n.digit < LENGTH(id_list) - LENGTH(REPLACE(id_list, ',', '')) + 1) AS ids;

  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

  OPEN id_cursor;

read_loop:
  LOOP
    FETCH id_cursor INTO id;
    IF done THEN
      LEAVE read_loop;
    END IF;

    DELETE
      FROM employee
    WHERE EmployeeId = id;
  END LOOP;

  CLOSE id_cursor;
END
$$

