  OR (veg.EmployeeCode COLLATE utf8mb4_unicode_ci
  LIKE CONCAT('%', querySearch, '%'))
  ORDER BY veg.CreatedDate DESC
  LIMIT recordsPerPage OFFSET pageOffset;


