CREATE PROCEDURE Proc_Account_Filter (IN inputSearch varchar(100), IN pageSize int, IN isRoot bool, IN pageOffset int, OUT totalRecord int, OUT totalRoot int)
SQL SECURITY INVOKER
COMMENT 'Thủ tục lọc tài khoản và phân trang'
BEGIN

  SELECT
    COUNT(a.AccountId) INTO totalRecord
  FROM account a
  WHERE (a.AccountCode LIKE CONCAT('%', inputSearch, '%')
  OR a.AccountCode LIKE CONCAT('%', inputSearch, '%'));


  SET totalRecord = IFNULL(totalRecord, 0); -- Đảm bảo TotalRecord không bị NULL

  SELECT
    COUNT(a.AccountId) INTO totalRoot
  FROM account a
  WHERE (a.AccountCode LIKE CONCAT('%', inputSearch, '%')
  OR a.AccountCode LIKE CONCAT('%', inputSearch, '%'))
  AND a.IsRoot = isRoot;


  SET totalRecord = IFNULL(totalRecord, 0); -- Đảm bảo TotalRecord không bị NULL

  SELECT
    vag.AccountId,
    vag.AccountCode,
    vag.AccountName,
    vag.AccountEnglishName,
    vag.AccountDescription,
    vag.AccountStatus,
    vag.AccountNature,
    vag.AccountParentId,
    vag.AccountParentCode,
    vag.Grade,
    vag.IsParent,
    vag.IsRoot,
    vag.CreatedDate,
    vag.CreatedBy,
    vag.ModifiedDate,
    vag.Modifiedby,
    vag.AccountObject
  FROM view_account_getall vag
  WHERE (vag.AccountName COLLATE utf8mb4_unicode_ci LIKE CONCAT('%', inputSearch, '%')
  OR vag.AccountCode COLLATE utf8mb4_unicode_ci LIKE CONCAT('%', inputSearch, '%'))
  AND (vag.IsRoot = isRoot
  OR isRoot IS NULL)
  ORDER BY vag.AccountCode LIMIT PageSize OFFSET pageOffset;
END
